<script>
import { ref, computed, watch } from "vue";
import { useRouter, useRoute } from "vue-router";
import { useStore } from "vuex";
import { useI18n } from "vue-i18n";
import apiConfig from "@/utils/apiConfig";
import permission from "@/components/permission.vue";
import withdraw from "@/components/withdraw.vue";
import withdrawTip from "@/components/withdrawTip.vue";
import promotionModal from "@/components/promotionModal.vue";
import { showNotify } from "@nutui/nutui";
import { get_withdraw_enable } from "@/apis/user";
import search from "@/components/search.vue";

export default {
  components: {
    permission,
    withdraw,
    promotionModal,
    withdrawTip,
    search,
  },
  setup() {
    const base_img_url = apiConfig.fileURL;
    const { t, locale: i18Language } = useI18n();
    const cur_language = ref(i18Language);
    let { state, commit, dispatch } = useStore();
    const route = useRoute();
    const router = useRouter();
    const active_tab = ref(1);
    const show_menu = ref(false);
    const showLanguageModal = ref(false);
    const show_exit = ref(false);

    const current_path_name = computed(() => {
      return route.name == "game"
        ? route.query.categoryName || route.query.companyName
        : route.name;
    });

    const wallet_infos = computed(() => {
      return state.permission.wallet_infos;
    });

    const user_infos = computed(() => {
      return state.permission.user_infos;
    });

    const is_login = computed(() => {
      return state.permission.is_login;
    });

    watch(
      is_login,
      (newval, oldval) => {
        if (!newval) {
          router.push({
            path: "/home",
          });
        }
      },
      { immediate: true, deep: true }
    );

    const tabSwitch = (e, index) => {
      switch (index) {
        case 0:
          router.push({
            path: "/vip",
          });
          break;
        case 1:
          router.push({
            path: "/home",
          });
          break;
        case 2:
          router.push({
            path: "/promotion",
          });
          break;
        case 3:
          router.push({
            path: "/invite",
          });
          break;
      }
    };

    const closeMenu = () => {
      show_menu.value = false;
    };

    const openMenu = () => {
      show_menu.value = true;
    };

    const goPersonalPage = (type) => {
      closeMenu();
      if (!state.permission.is_login) {
        commit("permission/show_permission_modal", { type: "login", show: true });
        return;
      }
      router.push({
        path: `/${type}`,
      });
    };

    const showPermission = (type) => {
      commit("permission/show_permission_modal", { type, show: true });
    };

    const showExit = () => {
      closeMenu();
      show_exit.value = !show_exit.value;
    };

    const confirmExit = () => {
      dispatch("permission/EXIT_LOGIN");
      if (state.common.recomend_promotion.length > 0)
        commit("common/set_show_promotion_modal", true);
    };

    const copyId = () => {
      var cInput = document.createElement("input");
      cInput.value = state.permission.user_infos.userAccount;
      document.body.appendChild(cInput);
      cInput.select();
      document.execCommand("copy");
      showNotify.text("a cópia foi bem-sucedida!", {
        color: "#fff",
        background: "#A0C4FD",
      });
      document.body.removeChild(cInput);
    };

    const goPage = (path) => {
      closeMenu();
      router.push({
        path: `/${path}`,
      });
    };

    const goGamePage = (name, id) => {
      closeMenu();
      router.push({
        path: "/game",
        query: {
          categoryId: id,
          categoryName: name,
        },
      });
    };

    const goGamePage2 = (path) => {
      closeMenu();
      if (state.permission.is_login) {
        router.push({
          path: "/game",
          query: {
            categoryId: "",
            categoryName: path,
          },
        });
      } else {
        commit("permission/show_permission_modal", { type: "login", show: true });
      }
    };

    const goDeposit = () => {
      router.push({
        path: "/deposit",
      });
    };

    const goWithdraw = () => {
      get_withdraw_enable
        .post("", {})
        .then((res) => {
          if (res.code == "000000") {
            commit("common/set_withdraw_type", 1);
            commit("common/set_show_withdraw_modal", true);
          } else if (res.code == "888888") {
            dispatch("permission/RESET_PERMISSION");
          } else if (res.code == "000010" || res.code == "000020") {
            commit("common/set_withdraw_tip_data", {
              code: res.code,
              ...res.data,
            });
            commit("common/set_show_withdraw_tip", true);
          } else {
            showNotify.danger(res.msg);
          }
        })
        .catch((err) => {
          dispatch("permission/RESET_PERMISSION");
        });
    };

    return {
      t,
      active_tab,
      route,
      base_img_url,
      is_login,
      show_menu,
      cur_language,
      showLanguageModal,
      current_path_name,
      show_exit,
      wallet_infos,
      user_infos,
      tabSwitch,
      closeMenu,
      goPersonalPage,
      showPermission,
      showExit,
      confirmExit,
      copyId,
      goPage,
      goGamePage,
      goGamePage2,
      goDeposit,
      goWithdraw,
      openMenu,
    };
  },
};
</script>

